import { React, useState, useEffect } from "react";
import { Bar } from "react-chartjs-2";
import axios from 'axios';

const Graph = (geoCode) => {
    const [healthWard, setHealthWard] = useState({});

    const url = 'http://covidradarmadrid-env.eba-wbgad2ub.eu-south-1.elasticbeanstalk.com/api/geocode/';

    console.log(`${url}${geoCode}`);

    const getData = (geoCode) => {
        axios.get(`${url}${geoCode}`)
        .then( response => {
            console.log(response.data);
            setHealthWard(response.data);
        })
        .catch( error => console.error(`Error: ${error}`))
    }

    useEffect( geoCode => {
        getData(geoCode);
    });   

    const toString = (date) => {
        const day = date[1];
        const month = date[2];
        const year = date[0];
        return day + "/" + month + "/" + year;
    }

    const dailyRecords = healthWard.dailyRecords;

    if (geoCode != '') {
    return (
        <>
        <Bar
            width={400}
            height={400}
            data={{
                labels: dailyRecords.map(dailyRecord => toString(dailyRecord.date)),
                datasets: [{
                    label: "Cases Last Two Weeks",
                    hoverBackgroundColor: "red",
                    backgroundColor: "pink",
                    data: dailyRecords.map(dailyRecord => dailyRecord.twoWeekCases),
                    barThickness: "flex",
                }]

            }}
            options={{
                maintainAspectRatio: false,
            }}

        />
        </>
    );
    }


}

export default Graph
