import axios from 'axios';
import { React, useState, useEffect } from 'react';
import Select from 'react-select';



const Form = ( { getGeoCode } ) => {
    const [options, setOptions] = useState([]);

    const urlOptions = 'http://covidradarmadrid-env.eba-wbgad2ub.eu-south-1.elasticbeanstalk.com/api/names+geocodes';

    const getOptions = () => {
        axios.get(urlOptions)
        .then((response) => {
            const allOptions = response.data;
            setOptions(convertOptions(allOptions));
        })
        .catch(error => console.error(`Error: ${error}`));
    }

    useEffect(() => {
        getOptions();
    }, []);

    const convertOptions = (options) => {
        var optionsArray = [];
        for (var option in options) {
            var name = option;
            var optionObject = { "label": name , "value": options.name}
            optionsArray.push(optionObject);
        }
        console.log(optionsArray);
        return optionsArray;
    }


    return(
        <div>
            <Select
                options={options}
                onChange={getGeoCode}
                autoFocus
                placeholder="Zona Basica de Salud"
                isSearchable
            />
        </div>
    );

}

export default Form
