import { React, useState, useEffect } from "react";
import { Bar } from "react-chartjs-2";
import axios from 'axios';

const Graph = (geoCode) => {
    const [healthWard, setHealthWard] = useState(null);

    const url = 'http://covidradarmadrid-env.eba-wbgad2ub.eu-south-1.elasticbeanstalk.com/api/geocode/';

    console.log(`${url}${geoCode}`);

    const getHealthWard = (geoCode) => {
        axios.get(`${url}${geoCode}`)
        .then( response => {
            console.log(response.data);
            setHealthWard(response.data);
        })
        .catch( error => console.error(`Error: ${error}`))
    }

    useEffect( geoCode => {
        getHealthWard(geoCode);
    });   

    const toString = (date) => {
        const day = date[1];
        const month = date[2];
        const year = date[0];
        return day + "/" + month + "/" + year;
    }

    const dailyRecords = healthWard.dailyRecords;

    const labels = dailyRecords.map(dailyRecord => toString(dailyRecord.date));

    const data = dailyRecords.map(dailyRecord => dailyRecord.twoWeekCases);


    if (geoCode != '') {
        return (
            <>
                <Bar
                    width={400}
                    height={400}
                    data={{
                        labels: {labels},
                        datasets: [{
                            label: "Cases Last Two Weeks",
                            hoverBackgroundColor: "red",
                            backgroundColor: "pink",
                            data: {data},
                            barThickness: "flex",
                        }]

                    }}
                    options={{
                        maintainAspectRatio: false,
                    }}

                />
            </>
        );
    } else {
        return (
            <div>

            </div>
        );
    }


}

export default Graph
